mapper.select:
SELECT id , customer_name customerName, SUM( number_packages * shoe_dual * sale_price - ( CASE WHEN price_spread IS NULL THEN 0 ELSE price_spread END ) * ( CASE WHEN difference_number IS NULL THEN 0 ELSE difference_number END ) - ( CASE WHEN sale_price IS NULL THEN 0 ELSE sale_price END ) * ( CASE WHEN returns_number IS NULL THEN 0 ELSE returns_number END ) ) totalGoodsMoney FROM zly_order WHERE 1 = 1
and customer_name = #{customerName}
GROUP BY customer_name limit #{startIndex},#{endIndex}

select count(0) from ( SELECT customer_name FROM zly_order WHERE 1 = 1
and customer_name = #{customerName}
GROUP BY customer_name ) temp

SELECT NAME customerName, SUM( ( CASE WHEN actual_payment IS NULL THEN 0 ELSE actual_payment END ) + ( CASE WHEN small_change IS NULL THEN 0 ELSE small_change END ) ) realBackMoney FROM zly_customer_payment_record WHERE 1 = 1
AND name = #{customerName}
GROUP BY NAME

select id , bill_start_time billStartTime , bill_end_time billEndTime , customary_dues customaryDues , actual_payment actualPayment , balance_due balanceDue , small_change snallChange , defective_goods defectiveGoods from zly_customer_payment_record where 1 = 1
AND name = #{customerName}
order by create_time desc limit #{startIndex},#{endIndex}

select count(0) totalRecord from zly_customer_payment_record where 1 = 1
AND name = #{customerName}
GROUP BY name
