mapper.select:
SELECT IFNULL(DATE_FORMAT(pay_time,'%Y-%m-%d'),'') pay_time, IFNULL(customer_name,'') customer_name, IFNULL(factory_name,'') factory_name, IFNULL(item_no,'') item_no, IFNULL(number_packages,0) number_packages, IFNULL(shoe_dual,0) shoe_dual, IFNULL(sale_price,0) sale_price, IFNULL((number_packages * shoe_dual * sale_price),0)totalMoney, IFNULL(remark,'') remark from zly_order where customer_is_defective_goods = 1
and customer_name = #{customerName}
AND DATE_FORMAT(pay_time ,'%Y-%m-%d') >= #{minCreateTime}
AND DATE_FORMAT(pay_time ,'%Y-%m-%d') <= #{maxCreateTime}
order by pay_time asc

SELECT IFNULL((sum(number_packages * shoe_dual * sale_price)),0) totalGoodsMoney, IFNULL((sum(number_packages * shoe_dual)),0) totalNum, IFNULL((sum(number_packages * shoe_dual * 2 )),0) jianci, IFNULL((sum(number_packages * shoe_dual * sale_price-(price_spread*difference_number) - (returns_number*sale_price) - (number_packages * shoe_dual * 2))),0) blanceDue FROM zly_order WHERE customer_name = #{customerName}
AND DATE_FORMAT(pay_time ,'%Y-%m-%d') >= #{minCreateTime}
AND DATE_FORMAT(pay_time ,'%Y-%m-%d') <=#{maxCreateTime}
and customer_is_defective_goods = 1

SELECT IFNULL(DATE_FORMAT(pay_time,'%Y-%m-%d'),'') pay_time, IFNULL(factory_name,'') factory_name, IFNULL(item_no,'') item_no, IFNULL(CONCAT(price_spread,' * ',difference_number),'') price_spread, IFNULL(CONCAT(returns_number,' * ',sale_price),'') returns_number, IFNULL(((price_spread*difference_number) + (returns_number*sale_price)) ,0) totalMoney FROM zly_order WHERE customer_name =#{customerName}
AND DATE_FORMAT(pay_time ,'%Y-%m-%d') >= #{minCreateTime}
AND DATE_FORMAT(pay_time ,'%Y-%m-%d') <=#{maxCreateTime}
AND (price_spread != 0 or difference_number != 0 or returns_number != 0 )

  SELECT
	  IFNULL(sum(((price_spread*difference_number) + (returns_number*sale_price))) ,0) totalMoney
	FROM
		zly_order
	WHERE
		customer_name =#{customerName}
	 
			AND DATE_FORMAT(pay_time ,'%Y-%m-%d') >= #{minCreateTime}
			
		  
			AND DATE_FORMAT(pay_time ,'%Y-%m-%d') <=#{maxCreateTime}
	
	AND (price_spread != 0 or difference_number != 0  or returns_number != 0 )

mapper.update:
  update
	 zly_order
	 
  		 SET customer_is_defective_goods =#{customerIsDefectiveGoods}
     
     
  		 SET factory_is_defective_goods = #{factoryIsDefectiveGoods}
     
     WHERE
		customer_name =#{customerName}
	 
			AND DATE_FORMAT(pay_time ,'%Y-%m-%d') >= #{minCreateTime}
			
		  
			AND DATE_FORMAT(pay_time ,'%Y-%m-%d') <=#{maxCreateTime}

mapper.select:
     SELECT
		count(id)
    from zly_order where and customer_name = #{customerName}
 	
      
 		AND DATE_FORMAT(pay_time ,'%Y-%m-%d') >= #{minCreateTime}
 		
    
    
      
 		AND DATE_FORMAT(pay_time ,'%Y-%m-%d') <= #{maxCreateTime}
 	
    
    AND (spred_status ==1 or return_status ==1 )
